import React, { Component } from "react";
import { Carousel } from "react-bootstrap";
import axios from "axios";
import Loader from "./Loader";
export default class ProductCarousel extends Component {
  constructor() {
    super();
    this.state = {
      topProduct: null,
    };
  }
  componentDidMount() {
    axios
      .get(`/api/products/top/product`)
      .then((res) => {
        console.log(res.data);
        this.setState({
          topProduct: res.data,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    if (this.state.topProduct === null) {
      return <Loader />;
    }
    return (
      <Carousel
        pause='hover'
        className='bg-dark '
        style={{ height: 400, marginBottom: 50 }}>
        {this.state.topProduct.map((p) => {
          return (
            <Carousel.Item style={{ width: "100%", height: "100%" }}>
              <img
                className='d-block w-100'
                src={p.image}
                alt={p.name}
                style={{ objectFit: "cover", height: 400 }}
              />
              <Carousel.Caption>
                <h2>{p.name}</h2>
              </Carousel.Caption>
            </Carousel.Item>
          );
        })}
      </Carousel>
    );
  }
}
