import React, { Component } from "react";
import { Carousel, Row, Col } from "react-bootstrap";
import axios from "axios";
import Loader from "./Loader";
import { Link } from "react-router-dom";

export default class PopularProducts extends Component {
  constructor() {
    super();
    this.state = {
      topProduct: null,
    };
  }
  componentDidMount() {
    axios
      .get(`/api/products/top/product`)
      .then((res) => {
        console.log(res.data);
        this.setState({
          topProduct: res.data,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    const { topProduct } = this.state;

    if (topProduct === null) {
      return <Loader />;
    }
    return (
      <>
        <h2>Latest Product</h2>

        <Row>
          <Col>
            <img
              src={topProduct[0].image}
              style={{
                width: "100%",
                height: 520,
                objectFit: "cover",
                cursor: "pointer",
              }}
            />
            <h1 style={{ position: "absolute", right: 20, bottom: 10 }}>
              Shop Now
            </h1>
          </Col>
          <Col>
            <img
              src={topProduct[1].image}
              style={{
                width: "100%",
                height: 250,
                objectFit: "cover",
                marginBottom: 20,
                cursor: "pointer",
              }}
            />
            <img
              src={topProduct[2].image}
              style={{
                width: "100%",
                height: 250,
                objectFit: "cover",
                cursor: "pointer",
              }}
            />
          </Col>
        </Row>
      </>
    );
  }
}
