import React, { Component } from "react";
import { Form, Button, Row, Col, Container } from "react-bootstrap";
import { Link } from "react-router-dom";
import FormContainer from "../component/FormContainer";
import { getUserDetails } from "../actions/userActions";
import { connect } from "react-redux";
import Loader from "../component/Loader";
import axios from "axios";

class ProductEditScreen extends Component {
  constructor() {
    super();
    this.state = {
      name: "",
      price: "",
      image: "",
      brand: "",
      category: "",
      countInStock: "",
      numReviews: 0,
      description: "",
      loading: true,
      uploading: false,
      selectedFile: null,
    };
  }

  // On file select (from the pop up)
  onFileChange = (event) => {
    // Update the state
    this.setState({ selectedFile: event.target.files[0] });
  };

  // On file upload (click the upload button)
  onFileUpload = () => {
    // Create an object of formData
    const formData = new FormData();

    // Update the formData object
    formData.append(
      "myFile",
      this.state.selectedFile,
      this.state.selectedFile.name
    );

    // Details of the uploaded file
    console.log(this.state.selectedFile);

    // Request made to the backend api
    // Send formData object
    axios.post("api/uploadfile", formData);
  };

  submitHandler = (e) => {
    this.setState({
      loading: true,
    });
    // e.preventDefault();
    const {
      name,
      price,
      image,
      brand,
      category,
      countInStock,
      numReviews,
      description,
    } = this.state;
    const data = {
      name,
      price,
      image,
      brand,
      category,
      countInStock,
      numReviews,
      description,
    };
    const token = this.props.getLoginInfoData.userInfo.token;
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    axios
      .put(
        `http://localhost:5000/api/products/${this.props.match.params.id}`,
        data,
        config
      )
      .then((response) => {
        this.props.history.push(`/admin/productslist`);
        this.setState({
          loading: false,
        });
      })
      .catch((error) => {
        console.log(error);
        this.setState({
          loading: false,
        });
      });
  };

  componentDidMount() {
    this.setState({
      loading: true,
    });
    const token = this.props.getLoginInfoData.userInfo.token;
    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };

    axios
      .get(
        `http://localhost:5000/api/products/${this.props.match.params.id}`,
        config
      )
      .then((response) => {
        console.log(response.data);
        this.setState({
          name: response.data.name,
          price: response.data.price,
          image: response.data.image,
          brand: response.data.brand,
          category: response.data.category,
          countInStock: response.data.countInStock,
          numReviews: response.data.numReviews,
          description: response.data.description,
          loading: false,
        });
      })
      .catch((error) => {
        console.log(error);
        this.setState({
          loading: false,
        });
      });
  }

  // fileUploadHandler = async (e) => {
  //   const file = e.target.files[0];
  //   const formData = new FormData();
  //   formData.append("image", file);
  //   this.setState({
  //     uploading: true,
  //   });
  //   try {
  //     const config = {
  //       headers: {
  //         "Content-Type": "multipart/form-data",
  //       },
  //     };

  //     await axios
  //       .post(`http://localhost:5000/api/uploads`, formData, config)
  //       .then((response) => {
  //         console.log("====================================");
  //         console.log(response.data);
  //         console.log("====================================");
  //         this.setState({
  //           uploading: false,
  //         });
  //       })
  //       .catch((err) => {
  //         console.log(err.response.message);
  //         this.setState({
  //           loading: false,
  //         });
  //       });

  //     this.setState({
  //       uploading: false,
  //     });
  //   } catch (error) {
  //     this.setState({
  //       uploading: false,
  //     });
  //   }
  // };

  render() {
    const {
      name,
      price,
      image,
      brand,
      category,
      countInStock,
      numReviews,
      description,
    } = this.state;
    return (
      <>
        <Link to='/admin/productslist' className='btn btn-light mt-5 ml-5'>
          <i className='fa fa-arrow-left'></i> Go back
        </Link>

        <FormContainer>
          <h2>Edit Product Details</h2>
          {this.state.loading ? (
            <Loader />
          ) : (
            <Form>
              <Form.Group controlId='formBasicEmail'>
                <Form.Label>Name</Form.Label>
                <Form.Control
                  type='text'
                  placeholder='Enter product name'
                  value={name}
                  onChange={(e) => this.setState({ name: e.target.value })}
                />
              </Form.Group>

              <Form.Group controlId='price'>
                <Form.Label>Price</Form.Label>
                <Form.Control
                  type='text'
                  placeholder='Enter Product Price'
                  value={price}
                  onChange={(e) => this.setState({ price: e.target.value })}
                />
              </Form.Group>

              <Form.Group controlId='category'>
                <Form.Label>Category</Form.Label>
                <Form.Control
                  type='text'
                  placeholder='Enter category'
                  value={category}
                  onChange={(e) => this.setState({ category: e.target.value })}
                />
              </Form.Group>

              <Form.Group controlId='category'>
                <Form.Label>Brand</Form.Label>
                <Form.Control
                  type='text'
                  placeholder='Enter category'
                  value={brand}
                  onChange={(e) => this.setState({ brand: e.target.value })}
                />
              </Form.Group>

              <Form.Group controlId='countInStock'>
                <Form.Label>Count In Stock</Form.Label>
                <Form.Control
                  type='text'
                  placeholder='Enter stock limit'
                  value={countInStock}
                  onChange={(e) =>
                    this.setState({ countInStock: e.target.value })
                  }
                />
              </Form.Group>

              <Form.Group controlId='description'>
                <Form.Label>Description</Form.Label>
                <Form.Control
                  type='text'
                  placeholder='Enter description'
                  value={description}
                  onChange={(e) =>
                    this.setState({ description: e.target.value })
                  }
                />
              </Form.Group>

              <Form.Group controlId='image'>
                <Form.Label> Image </Form.Label>
                <Form.Control
                  type='text'
                  placeholder='Enter image url'
                  value={image}
                  onChange={(e) => this.setState({ image: e.target.value })}
                />
                <Form.File
                  id='img-upload'
                  label='Choose File'
                  custom
                  onChange={this.fileUploadHandler}
                />

                {this.state.uploading && <Loader />}
              </Form.Group>

              {/* <Form.Group controlId='isAdmin'>
                <Form.Check
                  type='Checkbox'
                  label='Is Admin'
                  checked={isAdmin}
                  onChange={(e) => this.setState({ isAdmin: e.target.checked })}
                />
              </Form.Group> */}

              {false ? (
                <Loader />
              ) : (
                <Button
                  variant='primary'
                  type='submit'
                  onClick={this.submitHandler}>
                  Update
                </Button>
              )}
            </Form>
          )}
        </FormContainer>
      </>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    getLoginInfoData: state.userLogin,
    getUserDetailsData: state.getUserDetails,
  };
};

export default connect(mapStateToProps)(ProductEditScreen);
