import React, { Component } from "react";
import { Carousel } from "react-bootstrap";
import axios from "axios";
import Loader from "./Loader";
export default class ProductCarousel extends Component {
  constructor() {
    super();
    this.state = {
      topProduct: null,
    };
  }
  componentDidMount() {
    axios
      .get(`/api/products/top/product`)
      .then((res) => {
        console.log(res.data);
        this.setState({
          topProduct: res.data,
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    if (this.state.topProduct === null) {
      return <Loader />;
    }
    return (
      <Carousel pause='hover' className='bg-dark ' style={{ height: 200 }}>
        {this.state.topProduct.map((p) => {
          return (
            <Carousel.Item>
              <img className='d-block w-100' src={p.image} alt='First slide' />
              <Carousel.Caption>
                <h3>First slide label</h3>
                <p>
                  Nulla vitae elit libero, a pharetra augue mollis interdum.
                </p>
              </Carousel.Caption>
            </Carousel.Item>
          );
        })}
      </Carousel>
    );
  }
}
