import React, { Component } from "react";
import {
  Container,
  Row,
  Col,
  Image,
  Card,
  ListGroup,
  Button,
  Form,
} from "react-bootstrap";
import { connect } from "react-redux";
import axios from "axios";
import { listProductDetails } from "../actions/productActions";
import Loader from "./Loader";
import Message from "./Message";
import Rating from "./Rating";

class ProductDetails extends Component {
  constructor() {
    super();
    this.state = {
      Product: [],
      qty: 1,
    };
  }
  componentDidMount() {
    this.props.dispatch(listProductDetails(this.props.match.params.id));
  }

  addToCartHandler = () => {
    this.props.history.push(
      `/cart/${this.props.match.params.id}?qty=${this.state.qty}`
    );
  };
  render() {
    const { loading, error, product } = this.props.getProductDetailsData;

    return (
      <div className='py-5 px-5'>
        {loading ? (
          <Loader />
        ) : error ? (
          <Message />
        ) : (
          <>
            <Row>
              <Col sm={12} md={6} lg={4} xl={4}>
                <Image style={{ width: "100%" }} src={product.image} rounded />
              </Col>
              <Col sm={12} md={6} lg={4} xl={4}>
                <Card style={{ border: "none" }}>
                  <ListGroup variant='flush'>
                    <ListGroup.Item as='h3'>{product.name}</ListGroup.Item>
                    <ListGroup.Item>
                      {/* {product.rating} rating from {product.numReviews} reviews */}
                      <Rating
                        value={product.rating}
                        text={`${product.numReviews} reviews`}
                      />
                    </ListGroup.Item>
                    <ListGroup.Item>
                      Price:{" "}
                      <span style={{ fontWeight: 700 }}>${product.price}</span>
                    </ListGroup.Item>
                    <ListGroup.Item>{product.description}</ListGroup.Item>
                  </ListGroup>
                </Card>
              </Col>
              <Col sm={12} md={6} lg={4} xl={4}>
                <Card>
                  <ListGroup variant='flush'>
                    <ListGroup.Item>
                      <Row>
                        <Col>Price: </Col>
                        <Col>${product.price}</Col>
                      </Row>
                    </ListGroup.Item>
                    <ListGroup.Item>
                      <Row>
                        <Col>Status:</Col>
                        <Col>
                          {product.countInStock > 0 ? (
                            <span style={{ color: "green" }}>In Stock</span>
                          ) : (
                            <span style={{ color: "red" }}>Out of stock</span>
                          )}
                        </Col>
                      </Row>
                    </ListGroup.Item>

                    <ListGroup.Item>
                      <Row>
                        <Col>Qty:</Col>
                        <Col>
                          {product.countInStock > 0 ? (
                            <Form.Control
                              as='select'
                              value={this.state.qty}
                              onChange={(e) =>
                                this.setState({ qty: e.target.value })
                              }>
                              {[...Array(product.countInStock).keys()].map(
                                (x) => (
                                  <option key={x + 1} value={x + 1}>
                                    {x + 1}
                                  </option>
                                )
                              )}
                            </Form.Control>
                          ) : (
                            0
                          )}
                        </Col>
                      </Row>
                    </ListGroup.Item>

                    <ListGroup.Item>
                      <Button
                        onClick={this.addToCartHandler}
                        disabled={product.countInStock === 0}
                        style={{ width: "100%" }}
                        variant='dark'>
                        Add To Cart
                      </Button>
                    </ListGroup.Item>
                  </ListGroup>
                </Card>
              </Col>
            </Row>
            <Row className='py-5'>
              <Col md={6}>
                <h2>Reviews</h2>
                {product.reviews.length === 0 && <p>No reviews</p>}
              </Col>
            </Row>
          </>
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    getProductDetailsData: state.productDetails,
  };
};

export default connect(mapStateToProps)(ProductDetails);
